import React, { useState, useEffect, useMemo } from 'react';
import { MapContainer, TileLayer, Marker, Circle, useMap, Popup } from 'react-leaflet';
import API from './api'; // D√πng instance API chung
import useGeolocation from './hooks/useGeolocation'; // Gi·ªØ nguy√™n file hook c·ªßa b·∫°n
import { useAuth } from './context/AuthContext';
import { calculateDistance } from './utils/distance';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix icon Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// üìç T·∫°o icon t√πy ch·ªânh cho v·ªã tr√≠ ng∆∞·ªùi d√πng
const userLocationIcon = new L.DivIcon({
  html: '<div class="user-location-pin"></div>',
  className: 'user-location-icon-container', // Class cho container, kh√¥ng c·∫ßn style nhi·ªÅu
  iconSize: [24, 24], // K√≠ch th∆∞·ªõc c·ªßa icon
});

const hanoiPosition = [21.028511, 105.854199];

// --- COMPONENT HELPER: T·ª± ƒë·ªông di chuy·ªÉn b·∫£n ƒë·ªì ƒë·∫øn v·ªã tr√≠ ng∆∞·ªùi d√πng ---
const PanToUserLocation = ({ userLocation, isAdminMode }) => {
  const map = useMap();

  useEffect(() => {
    // Ch·ªâ pan map khi ·ªü User View v√† ƒë√£ c√≥ t·ªça ƒë·ªô h·ª£p l·ªá
    if (!isAdminMode && userLocation.loaded && !userLocation.error && userLocation.coordinates.lat) {
      map.setView([userLocation.coordinates.lat, userLocation.coordinates.lng], 15); // Zoom v√†o level 15
    }
  }, [userLocation, isAdminMode, map]);

  return null; // Component n√†y kh√¥ng render g√¨ c·∫£, ch·ªâ th·ª±c hi·ªán h√†nh ƒë·ªông
};

const LeafletMapComponent = () => {
  const { userRole } = useAuth();
  const isAdmin = userRole === 'admin';
  
  const [locations, setLocations] = useState([]);
  const [isAdminMode, setIsAdminMode] = useState(false); // Ch·∫ø ƒë·ªô xem c·ªßa Admin
  const [radius, setRadius] = useState(5); // B√°n k√≠nh t√¨m ki·∫øm (km)

  const userLocation = useGeolocation();
  const [selectedLocation, setSelectedLocation] = useState(null);

  // Khi role thay ƒë·ªïi, c·∫≠p nh·∫≠t ch·∫ø ƒë·ªô
  useEffect(() => {
    setIsAdminMode(isAdmin);
  }, [isAdmin]);

  // H√†m fetch d·ªØ li·ªáu
  const fetchLocations = async () => {
    try {
      let url = '/locations'; // M·∫∑c ƒë·ªãnh l·∫•y list public
      
      if (isAdminMode) {
        // Admin: L·∫•y danh s√°ch qu·∫£n tr·ªã (bao g·ªìm ch∆∞a duy·ªát)
        url = '/locations/admin/all';
      } else if (userLocation.loaded && userLocation.coordinates.lat) {
        // User/Guest: L·∫•y theo b√°n k√≠nh g·∫ßn ƒë√¢y
        const { lat, lng } = userLocation.coordinates;
        url = `/locations/nearby?lat=${lat}&lng=${lng}&radius=${radius}`;
      }

      const res = await API.get(url);
      setLocations(res.data.data || []);
    } catch (error) {
      console.error("Error fetching locations:", error);
    }
  };

  // G·ªçi API khi dependency thay ƒë·ªïi
  useEffect(() => {
    // N·∫øu l√† Admin mode -> g·ªçi lu√¥n
    // N·∫øu l√† User mode -> ch·ªù c√≥ v·ªã tr√≠ m·ªõi g·ªçi
    if (isAdminMode || (userLocation.loaded && !userLocation.error)) {
      fetchLocations();
    }
  }, [isAdminMode, userLocation.loaded, radius]);

  return (
    <div style={{ position: 'relative', height: '100%', width: '100%' }}>
      
      {/* Panel ƒëi·ªÅu khi·ªÉn (Filter Radius) */}
      {!isAdminMode && (
        <div style={{ position: 'absolute', top: 10, right: 10, zIndex: 1000, background: 'white', padding: 10, borderRadius: 5 }}>
          <label>T√¨m b√°n k√≠nh: {radius} km</label>
          <input 
            type="range" min="1" max="20" value={radius} 
            onChange={(e) => setRadius(e.target.value)} 
          />
        </div>
      )}

      {/* N√∫t Toggle Admin Mode */}
      {isAdmin && (
        <button
          onClick={() => setIsAdminMode(!isAdminMode)}
          class = "btn"
          style={{ position: 'absolute', top: 100, right: 10, zIndex: 1000, padding: '5px 10px' }}
        >
          {isAdminMode ? "Switch to User View" : "Switch to Admin View"}
        </button>
      )}

      <MapContainer center={hanoiPosition} zoom={13} style={{ height: '100%', width: '100%' }}>
        {/* üëá TH√äM COMPONENT HELPER V√ÄO ƒê√ÇY */}
        <PanToUserLocation userLocation={userLocation} isAdminMode={isAdminMode} />

        <TileLayer url="https://mt1.google.com/vt/lyrs=m&x={x}&y={y}&z={z}" attribution="Google Maps" />

        {/* Marker v·ªã tr√≠ ng∆∞·ªùi d√πng */}
        {!isAdminMode && userLocation.coordinates.lat && (
          <>
            <Marker 
              position={[userLocation.coordinates.lat, userLocation.coordinates.lng]}
              icon={userLocationIcon} // <-- √Åp d·ª•ng icon t√πy ch·ªânh
            >
              <Popup>B·∫°n ƒëang ·ªü ƒë√¢y</Popup>
            </Marker>
            <Circle center={[userLocation.coordinates.lat, userLocation.coordinates.lng]} radius={radius * 1000} />
          </>
        )}

        {/* Marker c√°c ƒë·ªãa ƒëi·ªÉm */}
        {locations.map(loc => (
          <Marker 
            key={loc.id} 
            position={[loc.latitude, loc.longitude]}
            eventHandlers={{ click: () => setSelectedLocation(loc) }}
          >
            <Popup>
               <b>{loc.name}</b> <br/>
               {loc.address} <br/>
               {isAdminMode && (loc.is_approved ? "‚úÖ ƒê√£ duy·ªát" : "‚ùå Ch∆∞a duy·ªát")}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default LeafletMapComponent;
