import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';

// Import Layout & Components
import MainLayout from './components/MainLayout'; // Layout mới tạo
import LeafletMapComponent from './MapContainer';
import LocationCRUD from './LocationCRUD';
import ProtectedRoute from './components/ProtectedRoute';
import { AuthProvider } from './context/AuthContext';

function App() {
  return (
    <Router>
      <AuthProvider>
        <Routes>
          {/* Route Mẹ: MainLayout (Chứa Header, Sidebar, Footer) */}
          <Route path="/" element={<MainLayout />}>
            
            {/* Route Con 1: Trang chủ (Hiển thị Map) */}
            {/* index nghĩa là path mặc định "/" */}
            <Route index element={<LeafletMapComponent />} />
            
            {/* Route Con 2: Admin (Được bảo vệ) */}
            <Route 
              path="admin" 
              element={
                <ProtectedRoute requiredRole="admin">
                  <div style={{ padding: '20px', overflowY: 'auto', height: '100%' }}>
                     <h2>⚙️ Quản lý địa điểm</h2>
                     <LocationCRUD />
                  </div>
                </ProtectedRoute>
              } 
            />

            {/* Route Con 3: Trang 404 */}
            <Route path="*" element={<Navigate to="/" />} />
            
          </Route>
        </Routes>
      </AuthProvider>
    </Router>
  );
}

export default App;
