import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export default function Sidebar({ isOpen, onCloseMobile }) {
  const { userRole } = useAuth();
  const location = useLocation();

  const menuItems = [
    { label: 'üè† Trang ch·ªß', path: '/' },
    { label: 'üìç T√¨m quanh ƒë√¢y', path: '/nearby' }, // B·∫°n c√≥ th·ªÉ t√°ch route n√†y n·∫øu mu·ªën
  ];

  if (userRole === 'admin') {
    menuItems.push({ label: '‚öôÔ∏è Qu·∫£n l√Ω ƒë·ªãa ƒëi·ªÉm', path: '/admin' });
  }

  return (
    <>
      {/* Overlay ƒëen m·ªù ch·ªâ hi·ªán ·ªü mobile khi sidebar m·ªü */}
      {isOpen && <div className="sidebar-overlay" onClick={onCloseMobile}></div>}

      <aside className={`app-sidebar ${isOpen ? 'open' : ''}`}>
        <ul className="sidebar-menu">
          {menuItems.map((item) => (
            <Link 
              to={item.path} 
              key={item.path} 
              className={`sidebar-item ${location.pathname === item.path ? 'active' : ''}`}
              onClick={onCloseMobile} // ƒê√≥ng sidebar khi ch·ªçn menu (tr√™n mobile)
            >
              {item.label}
            </Link>
          ))}
        </ul>
        
        <div style={{ marginTop: 'auto', padding: '20px', fontSize: '0.8rem', color: '#bdc3c7' }}>
          <p>Phi√™n b·∫£n 1.0.0</p>
        </div>
      </aside>
    </>
  );
}
