import React, { useState, useEffect, useMemo } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Circle, useMap } from 'react-leaflet';
import axios from 'axios';
import useGeolocation from './hooks/useGeolocation';
import { useAuth } from './context/AuthContext';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// ‚öôÔ∏è Fix default icon
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const hanoiPosition = [21.028511, 105.854199];
const USER_ZOOM = 15;
const ADMIN_ZOOM = 13;

// üîÑ Helper: thay ƒë·ªïi view khi center thay ƒë·ªïi
const ChangeView = ({ center, zoom }) => {
  const map = useMap();
  useEffect(() => {
    map.setView(center, zoom);
  }, [map, center, zoom]);
  return null;
};

const LeafletMapComponent = () => {
  const { userRole } = useAuth();
  const isAdmin = userRole === 'admin';

  const [locations, setLocations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const userLocation = useGeolocation();
  const [mapCenter, setMapCenter] = useState(hanoiPosition);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [isAdminMode, setIsAdminMode] = useState(false);

  // üü¢ Khi role thay ƒë·ªïi, n·∫øu l√† admin th√¨ b·∫≠t lu√¥n admin mode
  useEffect(() => {
    if (isAdmin) setIsAdminMode(true);
  }, [isAdmin]);

  // --- Debounce function ---
  const debounce = (func, delay) => {
    let timeoutId;
    return (...args) => {
      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => func(...args), delay);
    };
  };

  // --- Fetch locations ---
  const debouncedFetchLocations = useMemo(() => {
    return debounce(async (lat, lng, adminMode) => {
      try {
        setLoading(true);
        let url;

        if (adminMode) {
          url = 'http://localhost:5000/api/locations'; // üëë Admin: to√†n b·ªô ƒë·ªãa ƒëi·ªÉm
        } else {
          const radiusKm = 5;
          url = `http://localhost:5000/api/locations/nearby?lat=${lat}&lng=${lng}&radius=${radiusKm}`;
        }

        const response = await axios.get(url);
        setLocations(response.data.data || response.data);
        setError(null);
      } catch (err) {
        console.error('L·ªói khi t·∫£i ƒë·ªãa ƒëi·ªÉm:', err);
        setError('Kh√¥ng th·ªÉ t·∫£i d·ªØ li·ªáu ƒë·ªãa ƒëi·ªÉm.');
      } finally {
        setLoading(false);
      }
    }, 1000);
  }, []);

  // --- Khi user location c√≥ ho·∫∑c admin b·∫≠t ch·∫ø ƒë·ªô ---
  useEffect(() => {
    if (isAdminMode) {
      debouncedFetchLocations(null, null, true);
      setMapCenter(hanoiPosition);
      return;
    }

    if (!userLocation.loaded) return;

    const { lat, lng } = userLocation.coordinates;
    if (lat && lng) {
      setMapCenter([lat, lng]);
      debouncedFetchLocations(lat, lng, false);
    } else if (userLocation.error) {
      setLoading(false);
      setError(`Kh√¥ng th·ªÉ x√°c ƒë·ªãnh v·ªã tr√≠: ${userLocation.error}`);
    }
  }, [
    userLocation.loaded,
    userLocation.coordinates,
    userLocation.error,
    isAdminMode,
    debouncedFetchLocations,
  ]);

  // --- Toggle Admin/User mode ---
  const handleToggleMode = () => {
    if (!isAdmin) return; // ‚ùå Ch·∫∑n user th∆∞·ªùng
    setIsAdminMode((prev) => !prev);
  };

  if (loading) return <p>ƒêang t·∫£i d·ªØ li·ªáu b·∫£n ƒë·ªì...</p>;

  return (
    <div style={{ height: '800px', width: '100%', position: 'relative' }}>
      {error && <p style={{ color: 'red', padding: '10px' }}>{error}</p>}

      {/* üîò N√∫t chuy·ªÉn mode (ch·ªâ admin) */}
      {isAdmin && (
        <button
          onClick={handleToggleMode}
          className="toggle-mode-btn"
          title="Chuy·ªÉn ch·∫ø ƒë·ªô hi·ªÉn th·ªã"
          style={{
            position: 'absolute',
            top: 10,
            right: 10,
            zIndex: 999,
            padding: '8px 12px',
            borderRadius: '8px',
            background: isAdminMode ? '#d9534f' : '#0275d8',
            color: 'white',
            border: 'none',
            cursor: 'pointer',
          }}
        >
          {isAdminMode ? 'Admin Mode' : 'User Mode'}
        </button>
      )}

      <MapContainer
        center={mapCenter}
        zoom={isAdminMode ? ADMIN_ZOOM : USER_ZOOM}
        scrollWheelZoom
        style={{ height: '100%', width: '100%' }}
        maxZoom={20}
      >
        <ChangeView
          center={mapCenter}
          zoom={isAdminMode ? ADMIN_ZOOM : USER_ZOOM}
        />

        <TileLayer
          attribution='&copy; <a href="https://maps.google.com">Google Maps</a>'
          url="https://mt1.google.com/vt/lyrs=m&x={x}&y={y}&z={z}"
          maxZoom={20}
          maxNativeZoom={17}
        />

        {/* üë§ User mode hi·ªÉn th·ªã v·ªã tr√≠ hi·ªán t·∫°i + b√°n k√≠nh */}
        {!isAdminMode && userLocation.coordinates.lat && (
          <>
            <Marker position={[userLocation.coordinates.lat, userLocation.coordinates.lng]}>
              <Popup>V·ªã tr√≠ hi·ªán t·∫°i c·ªßa b·∫°n</Popup>
            </Marker>

            <Circle
              center={[userLocation.coordinates.lat, userLocation.coordinates.lng]}
              radius={1000}
              pathOptions={{
                color: 'blue',
                fillColor: 'lightblue',
                fillOpacity: 0.25,
              }}
            />
          </>
        )}

        {/* C√°c ƒë·ªãa ƒëi·ªÉm */}
        {locations.map((loc) => {
          const lat = parseFloat(loc.latitude);
          const lng = parseFloat(loc.longitude);
          if (isNaN(lat) || isNaN(lng)) return null;

          return (
            <Marker
              key={loc.id}
              position={[lat, lng]}
              title={loc.name}
              eventHandlers={{
                click: () => setSelectedLocation(loc),
              }}
            >
              <Popup>
                <strong>{loc.name}</strong>
                <br />
                {!isAdminMode && loc.distance_km && (
                  <span>Kho·∫£ng c√°ch: {loc.distance_km.toFixed(2)} km</span>
                )}
              </Popup>
            </Marker>
          );
        })}
      </MapContainer>

      {/* Modal chi ti·∫øt */}
      {selectedLocation && (
        <div
          className={`detail-modal ${selectedLocation.fadeOut ? 'fade-out' : ''}`}
          onAnimationEnd={() => {
            if (selectedLocation.fadeOut) setSelectedLocation(null);
          }}
        >
          <div
            className={`detail-content ${selectedLocation.fadeOut ? 'fade-out-content' : ''}`}
          >
            <button
              className="detail-close"
              onClick={() =>
                setSelectedLocation((prev) => ({ ...prev, fadeOut: true }))
              }
            >
              √ó
            </button>

            <h2>{selectedLocation.name}</h2>
            {selectedLocation.address && (
              <p>
                <strong>ƒê·ªãa ch·ªâ:</strong> {selectedLocation.address}
              </p>
            )}
            {selectedLocation.description && <p>{selectedLocation.description}</p>}
          </div>
        </div>
      )}
    </div>
  );
};

export default LeafletMapComponent;
