import React from 'react';
import { useAuth } from '../context/AuthContext';

export default function Header({ onToggleSidebar, onOpenAuth }) {
  const { user, logout } = useAuth();

  return (
    <header className="app-header">
      <div className="header-brand">
        {/* Nút Hamburger cho Mobile */}
        <button className="menu-toggle-btn" onClick={onToggleSidebar}>☰</button>
        <span>HaNoi FoodMap</span>
      </div>

      <div className="header-actions">
        {user ? (
          <div style={{ display: 'flex', gap: '15px', alignItems: 'center' }}>
            <span>Chào, <b>{user.username}</b></span>
            <button onClick={logout} className="logout-btn" style={{ background: '#e74c3c', border:'none', padding:'5px 10px', borderRadius:'4px', color:'white', cursor:'pointer'}}>Thoát</button>
          </div>
        ) : (
          <button onClick={onOpenAuth} style={{ background: '#2ecc71', border:'none', padding:'8px 15px', borderRadius:'4px', color:'white', cursor:'pointer'}}>Đăng nhập</button>
        )}
      </div>
    </header>
  );
}
