// frontend/src/pages/AuthModal.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './AuthModal.css';

export default function AuthModal({ onClose }) {
  const { login } = useAuth();
  const [isLogin, setIsLogin] = useState(true);
  const [isAdmin, setIsAdmin] = useState(false); // üëà th√™m
  const [form, setForm] = useState({ email: '', password: '', username: '' });
  const [message, setMessage] = useState('');

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      // üëá endpoint linh ho·∫°t h∆°n
      let endpoint;
      if (isLogin) {
        endpoint = isAdmin ? '/auth/admin/login' : '/auth/login';
      } else {
        endpoint = '/auth/register';
      }

      const { data } = await axios.post(`http://localhost:5000/api${endpoint}`, form);

      if (data.success && data.token) {
        login(data.token, data.role);
        localStorage.setItem('token', data.token);
        localStorage.setItem('role', data.role);
        onClose();
      } else if (data.success) {
        setMessage('ƒêƒÉng k√Ω th√†nh c√¥ng! H√£y ƒëƒÉng nh·∫≠p.');
        setIsLogin(true);
      }
    } catch (err) {
      setMessage(err.response?.data?.message || 'L·ªói k·∫øt n·ªëi m√°y ch·ªß.');
    }
  };

  return (
    <div className="auth-modal-overlay">
      <div className="auth-modal">
        <button className="close-btn" onClick={onClose}>√ó</button>
        <h2>{isLogin ? (isAdmin ? 'ƒêƒÉng nh·∫≠p Admin' : 'ƒêƒÉng nh·∫≠p') : 'ƒêƒÉng k√Ω'}</h2>

        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <input
              type="text"
              name="username"
              placeholder="T√™n ng∆∞·ªùi d√πng"
              value={form.username}
              onChange={handleChange}
              required
            />
          )}
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={form.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="M·∫≠t kh·∫©u"
            value={form.password}
            onChange={handleChange}
            required
          />

          <button
            type="submit"
            className="submit-btn"
          >
            {isLogin ? (isAdmin ? 'ƒêƒÉng nh·∫≠p Admin' : 'ƒêƒÉng nh·∫≠p') : 'ƒêƒÉng k√Ω'}
          </button>
        </form>

        {message && <p className="error">{message}</p>}

        {isLogin && (
          <p className="toggle-admin">
            <span onClick={() => setIsAdmin(!isAdmin)}>
              {isAdmin ? '‚Üê Quay l·∫°i ƒëƒÉng nh·∫≠p ng∆∞·ªùi d√πng' : 'ƒêƒÉng nh·∫≠p v·ªõi t∆∞ c√°ch Admin'}
            </span>
          </p>
        )}

        <p className="toggle">
          {isLogin ? (
            <>Ch∆∞a c√≥ t√†i kho·∫£n?{' '}
              <span onClick={() => setIsLogin(false)}>ƒêƒÉng k√Ω ngay</span>
            </>
          ) : (
            <>ƒê√£ c√≥ t√†i kho·∫£n?{' '}
              <span onClick={() => setIsLogin(true)}>ƒêƒÉng nh·∫≠p</span>
            </>
          )}
        </p>
      </div>
    </div>
  );
}
