import React from "react";

const LocationDetailModal = ({ location, onClose, theme = "light" }) => {
  if (!location) return null;

  // Determine colors based on theme
  const isDark = theme === "dark";
  const backgroundColor = isDark ? "#1e1e1e" : "#ffffff";
  const textColor = isDark ? "#f1f1f1" : "#222222";
  const borderColor = isDark ? "#333333" : "#e5e5e5";
  const xButtonColor = isDark ? "#f1f1f1" : "#444444";

  return (
    <div
      className="crud-container"
      style={{
        position: "fixed",
        top: "10%",
        left: "50%",
        transform: "translateX(-50%)",
        zIndex: 1000,
        maxWidth: "500px",
        width: "90%",
        backgroundColor,
        color: textColor,
        textAlign: "left",
        padding: "24px",
        borderRadius: "12px",
        boxShadow: "0 6px 18px rgba(0, 0, 0, 0.2)",
        border: `1px solid ${borderColor}`,
        animation: "fadeIn 0.2s ease-in-out",
      }}
    >
      {/* ‚úñÔ∏è Top-right close button */}
      <button
        onClick={onClose}
        style={{
          position: "absolute",
          top: "10px",
          right: "14px",
          background: "none",
          border: "none",
          fontSize: "22px",
          cursor: "pointer",
          color: xButtonColor,
          transition: "color 0.2s ease-in-out",
        }}
        onMouseEnter={(e) =>
          (e.target.style.color = isDark ? "#ff6b6b" : "#c0392b")
        }
        onMouseLeave={(e) => (e.target.style.color = xButtonColor)}
        aria-label="Close"
      >
        √ó
      </button>

      {/* üèôÔ∏è Location details */}
      <h2 style={{ marginBottom: "12px", fontWeight: "600" }}>
        {location.name}
      </h2>

      {location.address && (
        <p style={{ margin: "6px 0" }}>
          <strong>ƒê·ªãa ch·ªâ:</strong> {location.address}
        </p>
      )}

      {location.description && (
        <p style={{ margin: "6px 0" }}>
          <strong>M√¥ t·∫£:</strong> {location.description}
        </p>
      )}

      {location.phone && (
        <p style={{ margin: "6px 0" }}>
          <strong>S·ªë ƒëi·ªán tho·∫°i:</strong> {location.phone}
        </p>
      )}

      {location.website && (
        <p style={{ margin: "6px 0" }}>
          <strong>Website:</strong>{" "}
          <a
            href={location.website}
            target="_blank"
            rel="noopener noreferrer"
            style={{
              color: isDark ? "#6ab0ff" : "#0077ff",
              textDecoration: "none",
            }}
          >
            {location.website}
          </a>
        </p>
      )}

      {location.distance_km && (
        <p style={{ marginTop: "8px", opacity: 0.8 }}>
          üìç C√°ch b·∫°n kho·∫£ng {location.distance_km.toFixed(2)} km
        </p>
      )}
    </div>
  );
};

export default LocationDetailModal;
