import React, { useState, useEffect, useRef } from 'react';

const SimulationController = ({ initialPosition, onPositionChange }) => {
  // --- STATE ---
  const [position, setPosition] = useState(initialPosition);
  // ‚ú® State m·ªõi cho c√°c √¥ input, l∆∞u d∆∞·ªõi d·∫°ng chu·ªói ƒë·ªÉ ng∆∞·ªùi d√πng d·ªÖ d√†ng ch·ªânh s·ª≠a
  const [inputCoords, setInputCoords] = useState({
    lat: initialPosition.lat.toString(),
    lng: initialPosition.lng.toString(),
  });
  const [isCollapsed, setIsCollapsed] = useState(false);

  // --- STATE CHO VI·ªÜC K√âO TH·∫¢ PANEL ---
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [panelPosition, setPanelPosition] = useState({ x: 20, y: 150 });
  const panelRef = useRef(null);

  // --- EFFECTS ---
  // ƒê·ªìng b·ªô state n·ªôi b·ªô khi v·ªã tr√≠ t·ª´ props (b·∫£n ƒë·ªì) thay ƒë·ªïi
  useEffect(() => {
    setPosition(initialPosition);
    setInputCoords({ lat: initialPosition.lat.toString(), lng: initialPosition.lng.toString() });
  }, [initialPosition]);

  const handleMove = (latChange, lngChange) => {
    const newPos = {
      // S·ª≠ d·ª•ng parseFloat ƒë·ªÉ ƒë·∫£m b·∫£o ph√©p c·ªông s·ªë h·ªçc
      lat: parseFloat(position.lat) + latChange,
      lng: parseFloat(position.lng) + lngChange,
    };
    setPosition(newPos);
    onPositionChange(newPos);
  };

  // ‚ú® X·ª≠ l√Ω khi ng∆∞·ªùi d√πng nh·∫≠p v√†o √¥ t·ªça ƒë·ªô
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputCoords(prev => ({ ...prev, [name]: value }));
  };

  // ‚ú® X·ª≠ l√Ω khi ng∆∞·ªùi d√πng nh·∫•n n√∫t "ƒêi ƒë·∫øn" ho·∫∑c Enter
  const handleApplyCoords = (e) => {
    e.preventDefault(); // NgƒÉn form submit v√† t·∫£i l·∫°i trang
    const newLat = parseFloat(inputCoords.lat);
    const newLng = parseFloat(inputCoords.lng);

    if (!isNaN(newLat) && !isNaN(newLng)) {
      const newPos = { lat: newLat, lng: newLng };
      setPosition(newPos);
      onPositionChange(newPos);
    } else {
      alert("T·ªça ƒë·ªô kh√¥ng h·ª£p l·ªá!");
    }
  };

  // --- X·ª≠ l√Ω k√©o th·∫£ panel ---
  const onMouseDown = (e) => {
    setIsDragging(true);
    setDragStart({
      x: e.clientX - panelPosition.x,
      y: e.clientY - panelPosition.y,
    });
  };

  const onMouseMove = (e) => {
    if (!isDragging) return;
    setPanelPosition({
      x: e.clientX - dragStart.x,
      y: e.clientY - dragStart.y,
    });
  };

  const onMouseUp = () => {
    setIsDragging(false);
  };

  useEffect(() => {
    window.addEventListener('mousemove', onMouseMove);
    window.addEventListener('mouseup', onMouseUp);
    return () => {
      window.removeEventListener('mousemove', onMouseMove);
      window.removeEventListener('mouseup', onMouseUp);
    };
  }, [isDragging]);

  const moveStep = 0.001; // B∆∞·ªõc nh·∫£y t·ªça ƒë·ªô

  return (
    <div
      ref={panelRef}
      className={`simulation-panel ${isCollapsed ? 'collapsed' : ''}`}
      style={{ top: `${panelPosition.y}px`, left: `${panelPosition.x}px` }}
    >
      <div className="simulation-header" onMouseDown={onMouseDown}>
        <span>üïπÔ∏è Gi·∫£ l·∫≠p di chuy·ªÉn</span>
        <button 
          className="toggle-collapse-btn" 
          onClick={() => setIsCollapsed(!isCollapsed)}
          title={isCollapsed ? "M·ªü r·ªông" : "Thu g·ªçn"}
        >
          {isCollapsed ? '‚äï' : '‚àí'}
        </button>
      </div>
      {!isCollapsed && (
        <div className="simulation-body">
          <div className="coords-display">
            Lat: {position.lat.toFixed(4)}, Lng: {position.lng.toFixed(4)}
          </div>

          {/* ‚ú® FORM NH·∫¨P T·ªåA ƒê·ªò M·ªöI */}
          <form onSubmit={handleApplyCoords} className="coord-input-form">
            <div className="coord-input-group">
              <input
                type="number" step="any" name="lat"
                value={inputCoords.lat} onChange={handleInputChange}
                placeholder="Vƒ© ƒë·ªô"
              />
              <input
                type="number" step="any" name="lng"
                value={inputCoords.lng} onChange={handleInputChange}
                placeholder="Kinh ƒë·ªô"
              />
            </div>
            <button type="submit" className="btn-apply-coords">ƒêi ƒë·∫øn</button>
          </form>
          <div className="move-controls">
            <button className="north" onClick={() => handleMove(moveStep, 0)} title="Di chuy·ªÉn l√™n B·∫Øc">‚Üë</button>
            <button className="west" onClick={() => handleMove(0, -moveStep)} title="Di chuy·ªÉn sang T√¢y">‚Üê</button>
            <div className="center"></div> 
            <button className="east" onClick={() => handleMove(0, moveStep)} title="Di chuy·ªÉn sang ƒê√¥ng">‚Üí</button>
            <button className="south" onClick={() => handleMove(-moveStep, 0)} title="Di chuy·ªÉn xu·ªëng Nam">‚Üì</button>
          </div>
          <button className="reset-btn" onClick={() => onPositionChange(null)}>
            Reset v·ªÅ v·ªã tr√≠ th·∫≠t
          </button>
        </div>
      )}
    </div>
  );
};

export default SimulationController;
