    setRoute(null); // X√≥a tuy·∫øn ƒë∆∞·ªùng c≈©
    setRouteSummary(null); // X√≥a summary c≈©

    const url = `https://api.openrouteservice.org/v2/directions/driving-car?api_key=${ORS_API_KEY}&start=${start.lng},${start.lat}&end=${end.longitude},${end.latitude}`;

    try {
      const response = await fetch(url);
      const data = await response.json();

      if (data.features && data.features.length > 0) {
        const feature = data.features[0];
        // Geometry tr·∫£ v·ªÅ l√† [lng, lat], c·∫ßn ƒë·∫£o ng∆∞·ª£c cho Leaflet [lat, lng]
