// frontend/src/LocationCRUD.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './context/AuthContext'; 

const API_URL = 'http://localhost:5000/api/locations';

const LocationCRUD = () => {
  const [locations, setLocations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [error, setError] = useState(null);
  
  const { userRole, authHeaders } = useAuth();

  // Chỉ Admin mới được thấy và thao tác
  if (userRole !== 'admin') {
    return null;
  }
  
  // Hàm Tải Dữ liệu (Sử dụng API lấy ALL)
  const fetchLocations = async () => {
    try {
      setLoading(true);
      // Dùng API public để lấy danh sách (GET /api/locations)
      const response = await axios.get(API_URL);
      setLocations(response.data.data);
      setLoading(false);
    } catch (err) {
      setError("Lỗi khi tải danh sách địa điểm.");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchLocations();
  }, []);
  
  // Xử lý Thay đổi Form
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  
  // Xử lý Submit (CREATE hoặc UPDATE)
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Chuẩn bị dữ liệu (đảm bảo latitude/longitude là số)
    const dataToSend = {
        ...formData,
        latitude: parseFloat(formData.latitude),
        longitude: parseFloat(formData.longitude)
    };

    try {
        if (isEditing) {
            // UPDATE: PUT /api/locations/:id
            await axios.put(`${API_URL}/${formData.id}`, dataToSend, { headers: authHeaders });
        } else {
            // CREATE: POST /api/locations
            await axios.post(API_URL, dataToSend, { headers: authHeaders });
        }
        
        // Reset form và tải lại danh sách
        setIsEditing(false);
        setFormData({});
        fetchLocations();
    } catch (err) {
        console.error("Lỗi CRUD:", err.response ? err.response.data : err);
        setError(`Thao tác thất bại: ${err.response?.data?.message || 'Lỗi kết nối hoặc Token không hợp lệ.'}`);
    }
  };
  
  // Xử lý Xóa (DELETE)
  const handleDelete = async (id) => {
    if (!window.confirm('Bạn có chắc chắn muốn xóa địa điểm này không?')) return;
    setError(null);
    try {
        // DELETE /api/locations/:id
        await axios.delete(`${API_URL}/${id}`, { headers: authHeaders });
        fetchLocations();
    } catch (err) {
        console.error("Lỗi xóa:", err.response ? err.response.data : err);
        setError(`Xóa thất bại: ${err.response?.data?.message || 'Lỗi kết nối hoặc Token không hợp lệ.'}`);
    }
  };

  // Khởi tạo form Thêm mới/Chỉnh sửa
  const handleEdit = (location = {}) => {
    setIsEditing(!!location.id); // true nếu có ID
    setFormData(location.id ? location : { name: '', address: '', latitude: '', longitude: '', phone_number: '', description: '', min_price: '', max_price: '', district: '' });
  };


  if (loading) return <p>Đang tải danh sách Admin...</p>;

  // 4. Render Giao diện
  return (
    <div style={{ margin: '20px', padding: '20px', border: '1px solid #ccc' }}>
      <h2>[ADMIN] Quản lý Địa điểm</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      
      {/* Nút Thêm mới */}
      <button onClick={() => handleEdit()} style={{ marginBottom: '20px', padding: '10px' }}>
        + Thêm Địa điểm Mới
      </button>

      {/* Form Thêm/Sửa */}
      {(formData.id || !isEditing) && (
        <form onSubmit={handleSubmit} style={{ border: '1px solid #eee', padding: '15px', marginBottom: '20px', textAlign: 'left' }}>
          <h3>{isEditing ? 'Chỉnh sửa Địa điểm' : 'Thêm Địa điểm'} (ID: {formData.id || 'MỚI'})</h3>
          
          <label>Tên Quán: <input type="text" name="name" value={formData.name || ''} onChange={handleChange} required /></label><br/>
          <label>Địa chỉ: <input type="text" name="address" value={formData.address || ''} onChange={handleChange} required /></label><br/>
          <label>Quận: <input type="text" name="district" value={formData.district || ''} onChange={handleChange} required /></label><br/>
          <label>Lat: <input type="number" step="any" name="latitude" value={formData.latitude || ''} onChange={handleChange} required /></label><br/>
          <label>Lng: <input type="number" step="any" name="longitude" value={formData.longitude || ''} onChange={handleChange} required /></label><br/>
          <label>Mô tả: <textarea name="description" value={formData.description || ''} onChange={handleChange}></textarea></label><br/>
          
          <button type="submit">{isEditing ? 'Cập nhật' : 'Thêm'}</button>
          <button type="button" onClick={() => setFormData({})} style={{ marginLeft: '10px' }}>Hủy</button>
        </form>
      )}

      {/* Danh sách Địa điểm */}
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Tên Quán</th>
            <th>Tọa độ</th>
            <th>Quận</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {locations.map((loc) => (
            <tr key={loc.id} style={{ borderBottom: '1px solid #eee' }}>
              <td>{loc.id}</td>
              <td style={{ textAlign: 'left' }}>{loc.name}</td>
              <td>{loc.latitude}, {loc.longitude}</td>
              <td>{loc.district}</td>
              <td>
                <button onClick={() => handleEdit(loc)}>Sửa</button>
                <button onClick={() => handleDelete(loc.id)} style={{ marginLeft: '10px', color: 'red' }}>Xóa</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default LocationCRUD;
