import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import API from '../api';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet'; 
import './LocationDetailPage.css';
import { useAuth } from '../context/AuthContext'; // ‚ú® TH√äM D√íNG N√ÄY

// Fix l·ªói icon c·ªßa Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const LocationDetailPage = () => {
  const { id } = useParams(); // L·∫•y ID t·ª´ URL
  const { search } = useLocation(); // L·∫•y query params t·ª´ URL (v√≠ d·ª•: ?view=user)
  const navigate = useNavigate(); // Hook ƒë·ªÉ ƒëi·ªÅu h∆∞·ªõng
  const [location, setLocation] = useState(null);
  const { userRole } = useAuth(); // ‚ú® L·∫§Y VAI TR√í USER

  // ‚ú® LOGIC M·ªöI: KI·ªÇM TRA QUY·ªÄN ADMIN V√Ä CH·∫æ ƒê·ªò XEM
  const queryParams = new URLSearchParams(search);
  const isUserViewForced = queryParams.get('view') === 'user';
  const isAdmin = userRole === 'admin' && !isUserViewForced;

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchLocation = async () => {
      try {
        setLoading(true);
        // G·ªçi API ƒë·ªÉ l·∫•y chi ti·∫øt ƒë·ªãa ƒëi·ªÉm theo ID
        const response = await API.get(`/locations/${id}`);
        setLocation(response.data.data);
        setError('');
      } catch (err) {
        console.error('L·ªói t·∫£i chi ti·∫øt ƒë·ªãa ƒëi·ªÉm:', err);
        setError('Kh√¥ng th·ªÉ t·∫£i th√¥ng tin ƒë·ªãa ƒëi·ªÉm. Vui l√≤ng th·ª≠ l·∫°i.');
      } finally {
        setLoading(false);
      }
    };

    fetchLocation();
  }, [id]); // Ch·∫°y l·∫°i effect khi ID thay ƒë·ªïi

  if (loading) {
    return <div className="detail-page-loading">‚è≥ ƒêang t·∫£i th√¥ng tin...</div>;
  }

  if (error) {
    return <div className="detail-page-error">‚ùå {error}</div>;
  }

  if (!location) {
    return <div className="detail-page-error">Kh√¥ng t√¨m th·∫•y ƒë·ªãa ƒëi·ªÉm.</div>;
  }

  const position = [location.latitude, location.longitude];

  return (
    <div className="location-detail-page">
      <div className="detail-header">
        <button onClick={() => navigate(-1)} className="back-button">
          &larr; Quay l·∫°i
        </button>
        <h1>{location.name}</h1>
      </div>

      <div className="detail-content-layout">
        <div className="detail-info-panel">
          <h3>Th√¥ng tin chi ti·∫øt</h3>
          <p><strong>üìç ƒê·ªãa ch·ªâ:</strong> {location.address}, {location.district}</p>
          {location.description && <p><strong>üìù M√¥ t·∫£:</strong> {location.description}</p>}
          {location.phone_number && <p><strong>üìû ƒêi·ªán tho·∫°i:</strong> {location.phone_number}</p>}
          {(location.min_price > 0 || location.max_price > 0) && (
            <p>
              <strong>üí∞ M·ª©c gi√°:</strong> {location.min_price.toLocaleString()} - {location.max_price.toLocaleString()} VNƒê
            </p>
          )}
          {/* ‚ú® CH·ªà HI·ªÇN TH·ªä TR·∫†NG TH√ÅI CHO ADMIN */}
          {isAdmin && (
            <p>
              <strong>Tr·∫°ng th√°i:</strong>
              <span className={`status-badge ${location.is_approved ? 'approved' : 'pending'}`}>{location.is_approved ? 'ƒê√£ duy·ªát' : 'Ch·ªù duy·ªát'}</span>
            </p>
          )}
        </div>

        <div className="detail-map-panel">
          <MapContainer center={position} zoom={16} scrollWheelZoom={false} className="detail-map">
            <TileLayer
              url="https://mt1.google.com/vt/lyrs=m&x={x}&y={y}&z={z}"
              attribution="Google Maps"
            />
            <Marker position={position}>
              <Popup>{location.name}</Popup>
            </Marker>
          </MapContainer>
        </div>
      </div>

          {/* ‚ú® M·ª§C H√åNH ·∫¢NH C·ª¶A QU√ÅN */}
          {location.images && location.images.length > 0 && (
            <div className="detail-section">
              <h4>üì∑ H√¨nh ·∫£nh</h4>
              <div className="image-gallery-container">
                {location.images.map((image, index) => (
                  <a key={image.id || index} href={image.url} target="_blank" rel="noopener noreferrer">
                    <img 
                      src={image.url} 
                      alt={`${location.name} - ·∫£nh ${index + 1}`} 
                      className="gallery-image" 
                    />
                  </a>
                ))}
              </div>
            </div>
          )}

          {/* ‚ú® M·ª§C TH·ª∞C ƒê∆†N C√ÅC M√ìN ƒÇN */}
          {location.menu && location.menu.length > 0 && (
            <div className="detail-section">
              <h4>üìú Th·ª±c ƒë∆°n</h4>
              <ul className="menu-list">
                {location.menu.map((item, index) => (
                  <li key={index} className="menu-item">
                    <span className="menu-item-name">{item.name}</span>
                    <span className="menu-item-price">{item.price.toLocaleString()} VNƒê</span>
                  </li>
                ))}
              </ul>
            </div>
          )}
      {/* =========================================
          PH·∫¶N ƒê√ÅNH GI√Å C·ª¶A KH√ÅCH H√ÄNG
          ========================================= */}
      <div className="detail-section reviews-section">
        <h4>‚≠ê ƒê√°nh gi√° t·ª´ c·ªông ƒë·ªìng</h4>
        <div className="review-summary">
          <span className="avg-rating">{location.average_rating ? location.average_rating.toFixed(1) : 'Ch∆∞a c√≥'}</span>
          <span className="avg-rating">{location.average_rating ? Number(location.average_rating).toFixed(1) : 'Ch∆∞a c√≥'}</span>
          <span className="review-count">({location.review_count || 0} l∆∞·ª£t ƒë√°nh gi√°)</span>
        </div>
        {/* TODO: Th√™m form g·ª≠i review v√† danh s√°ch review ·ªü ƒë√¢y */}
      </div>
    </div>
  );
};

export default LocationDetailPage;
