  // Debug ƒë·ªÉ xem n√≥ nh·∫≠n ƒë∆∞·ª£c g√¨ (F12 -> Console)
  console.log("üõ°Ô∏è Protected Check:", { authToken, userRole, requiredRole });

  // Ki·ªÉm tra ƒëƒÉng nh·∫≠p
  if (!authToken) {
      return <Navigate to="/" replace />;
  }

  // Ki·ªÉm tra quy·ªÅn (N·∫øu y√™u c·∫ßu admin m√† userRole kh√¥ng ph·∫£i admin)
  if (requiredRole && userRole !== requiredRole) {
    console.warn("‚õî Sai quy·ªÅn! Chuy·ªÉn h∆∞·ªõng sang Unauthorized");
    return <Navigate to="/unauthorized" replace />;
  }

  return children;
}
