// frontend/src/pages/AuthModal.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './AuthModal.css';

// Cấu hình API URL (Dùng IP LAN nếu test điện thoại, hoặc localhost)
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
// const API_URL = 'http://192.168.1.X:5000/api'; // Uncomment và sửa IP nếu cần test đt

export default function AuthModal({ onClose }) {
  const { login } = useAuth();
  
  // State quản lý
  const [isLogin, setIsLogin] = useState(true);   // True: Đăng nhập, False: Đăng ký
  const [isAdmin, setIsAdmin] = useState(false);  // True: Admin Mode, False: User Mode
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  
  const [form, setForm] = useState({ email: '', password: '', username: '' });

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  // Hàm submit rút gọn và tách biệt rõ ràng
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (loading) return;
    
    setLoading(true);
    setMessage('');

    try {
      // 1. XÁC ĐỊNH ENDPOINT (Tách biệt hoàn toàn)
      let endpoint = '';
      
      if (!isLogin) {
        // Nếu là Đăng ký -> Luôn gọi User Register (Admin thường không cho đăng ký public)
        endpoint = '/auth/register';
      } else {
        // Nếu là Đăng nhập -> Phụ thuộc vào biến isAdmin
        endpoint = isAdmin ? '/auth/admin/login' : '/auth/login';
      }

      // 2. GỌI API
      const { data } = await axios.post(`${API_URL}${endpoint}`, form, {
        headers: { 'Content-Type': 'application/json' }
      });

      // 3. XỬ LÝ KẾT QUẢ
      if (data.success) {
        if (isLogin && data.token) {
          // Đăng nhập thành công
          login(data.token, data.role || (isAdmin ? 'admin' : 'user'));
          onClose();
        } else {
          // Đăng ký thành công
          setMessage('Đăng ký thành công! Vui lòng đăng nhập.');
          setIsLogin(true); // Chuyển về form đăng nhập
          setIsAdmin(false); // Mặc định về User
        }
      }
      
    } catch (err) {
      // Xử lý lỗi chung
      const errorMsg = err.response?.data?.message || 'Lỗi kết nối hoặc sai thông tin.';
      setMessage(errorMsg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-modal-overlay">
      <div className={`auth-modal ${isAdmin ? 'admin-theme' : ''}`}>
        <button className="close-btn" onClick={onClose}>×</button>

        {/* HEADER: Hiển thị rõ đang ở chế độ nào */}
        <h2 style={{ color: isAdmin ? '#d9534f' : '#333' }}>
          {isLogin 
            ? (isAdmin ? 'QUẢN TRỊ VIÊN' : 'ĐĂNG NHẬP') 
            : 'ĐĂNG KÝ'}
        </h2>
        
        {isAdmin && isLogin && (
          <p style={{fontSize: '0.9em', color: '#666', marginBottom: '15px'}}>
            Đang đăng nhập vào hệ thống quản lý
          </p>
        )}

        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <input
              type="text"
              name="username"
              placeholder="Tên hiển thị"
              value={form.username}
              onChange={handleChange}
              required
            />
          )}
          <input
            type="email"
            name="email"
            placeholder={isAdmin ? "Email quản trị" : "Email"}
            value={form.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Mật khẩu"
            value={form.password}
            onChange={handleChange}
            required
          />

          <button
            type="submit"
            className="submit-btn"
            disabled={loading}
            style={{ 
                backgroundColor: isAdmin && isLogin ? '#d9534f' : '#0275d8',
                opacity: loading ? 0.7 : 1 
            }}
          >
            {loading 
              ? 'Đang xử lý...' 
              : (isLogin ? (isAdmin ? 'Vào trang quản trị' : 'Đăng nhập') : 'Đăng ký')}
          </button>
        </form>

        {message && <p className="error">{message}</p>}

        {/* TOGGLE CHẾ ĐỘ ADMIN/USER - Tách biệt rõ ràng */}
        {isLogin && (
          <div className="mode-switch" style={{ marginTop: '15px', borderTop: '1px solid #eee', paddingTop: '10px' }}>
            <label style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px', cursor: 'pointer' }}>
              <input 
                type="checkbox" 
                checked={isAdmin} 
                onChange={() => {
                    setIsAdmin(!isAdmin);
                    setMessage(''); // Xóa lỗi cũ khi chuyển chế độ
                }} 
              />
              <span style={{ fontSize: '0.9em', color: '#555' }}>
                Đăng nhập với quyền Admin
              </span>
            </label>
          </div>
        )}

        <p className="toggle">
          {isLogin ? (
            <>Chưa có tài khoản? <span onClick={() => { setIsLogin(false); setIsAdmin(false); }}>Đăng ký ngay</span></>
          ) : (
            <>Đã có tài khoản? <span onClick={() => setIsLogin(true)}>Đăng nhập</span></>
          )}
        </p>
      </div>
    </div>
  );
}
