// frontend/src/pages/AuthModal.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './AuthModal.css'; // üëà th√™m d√≤ng n√†y

export default function AuthModal({ onClose }) {
  const { login } = useAuth();
  const [isLogin, setIsLogin] = useState(true);
  const [form, setForm] = useState({ email: '', password: '', username: '' });
  const [message, setMessage] = useState('');

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      const endpoint = isLogin ? '/auth/login' : '/auth/register';
      const { data } = await axios.post(`http://localhost:5000/api${endpoint}`, form);

      if (data.success && data.token) {
        login(data.token, data.role);
        localStorage.setItem('token', data.token);
        localStorage.setItem('role', data.role);
        onClose();
      } else if (data.success) {
        setMessage('ƒêƒÉng k√Ω th√†nh c√¥ng! H√£y ƒëƒÉng nh·∫≠p.');
        setIsLogin(true);
      }
    } catch (err) {
      setMessage(err.response?.data?.message || 'L·ªói k·∫øt n·ªëi m√°y ch·ªß.');
    }
  };

  return (
    <div className="auth-modal-overlay">
      <div className="auth-modal">
        <button className="close-btn" onClick={onClose}>√ó</button>
        <h2>{isLogin ? 'ƒêƒÉng nh·∫≠p' : 'ƒêƒÉng k√Ω'}</h2>

        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <input
              type="text"
              name="username"
              placeholder="T√™n ng∆∞·ªùi d√πng"
              value={form.username}
              onChange={handleChange}
              required
            />
          )}
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={form.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="M·∫≠t kh·∫©u"
            value={form.password}
            onChange={handleChange}
            required
          />
          <button
  type="submit"
  className="w-full mt-3 py-3 text-white font-semibold rounded-xl 
             bg-gradient-to-r from-blue-600 to-indigo-500
             shadow-lg shadow-blue-500/30 
             hover:from-indigo-500 hover:to-blue-600
             transform hover:-translate-y-0.5 
             transition-all duration-200"
>
  {isLogin ? 'ƒêƒÉng nh·∫≠p' : 'ƒêƒÉng k√Ω'}
</button>

        </form>

        {message && <p className="error">{message}</p>}

        <p className="toggle">
          {isLogin ? (
            <>Ch∆∞a c√≥ t√†i kho·∫£n?{' '}
              <span onClick={() => setIsLogin(false)}>ƒêƒÉng k√Ω ngay</span>
            </>
          ) : (
            <>ƒê√£ c√≥ t√†i kho·∫£n?{' '}
              <span onClick={() => setIsLogin(true)}>ƒêƒÉng nh·∫≠p</span>
            </>
          )}
        </p>
      </div>
    </div>
  );
}
