// frontend/src/MapContainer.js
import React, { useState, useEffect, useMemo } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Circle, useMap } from 'react-leaflet';
import axios from 'axios';
import useGeolocation from './hooks/useGeolocation';
import { useAuth } from './context/AuthContext';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import { Button } from '@/components/ui/button';

// ‚öôÔ∏è Fix default icon
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const hanoiPosition = [21.028511, 105.854199];
const DEFAULT_ZOOM_USER = 15;
const DEFAULT_ZOOM_ADMIN = 13;

// Smooth fly animation
const SmoothChangeView = ({ center, zoom }) => {
  const map = useMap();
  useEffect(() => {
    map.flyTo(center, zoom, { animate: true, duration: 1.6 });
  }, [center, zoom, map]);
  return null;
};

const LeafletMapComponent = () => {
  const { userRole } = useAuth(); // üîí l·∫•y vai tr√≤ t·ª´ context (Admin/User)
  const isRealAdmin = userRole === 'Admin';

  const [isAdminMode, setIsAdminMode] = useState(false); // admin c√≥ th·ªÉ b·∫≠t t·∫Øt
  const [locations, setLocations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const userLocation = useGeolocation();
  const [mapCenter, setMapCenter] = useState(hanoiPosition);
  const [zoomLevel, setZoomLevel] = useState(DEFAULT_ZOOM_USER);
  const [selectedLocation, setSelectedLocation] = useState(null);

  // Debounce helper
  const debounce = (func, delay) => {
    let timeoutId;
    return (...args) => {
      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => func(...args), delay);
    };
  };

  // Fetch data
  const debouncedFetchLocations = useMemo(() => {
    return debounce(async (lat, lng, adminMode) => {
      try {
        setLoading(true);
        const url = adminMode
          ? `http://localhost:5000/api/locations`
          : `http://localhost:5000/api/locations/nearby?lat=${lat}&lng=${lng}&radius=1`;

        const response = await axios.get(url);
        setLocations(response.data.data || []);
        setError(null);
      } catch (err) {
        console.error('L·ªói khi t·∫£i ƒë·ªãa ƒëi·ªÉm:', err);
        setError('Kh√¥ng th·ªÉ t·∫£i danh s√°ch ƒë·ªãa ƒëi·ªÉm.');
      } finally {
        setLoading(false);
      }
    }, 800);
  }, []);

  // C·∫≠p nh·∫≠t map khi mode thay ƒë·ªïi
  useEffect(() => {
    if (isAdminMode) {
      setMapCenter(hanoiPosition);
      setZoomLevel(DEFAULT_ZOOM_ADMIN);
      debouncedFetchLocations(null, null, true);
      return;
    }

    if (!userLocation.loaded) return;
    const { lat, lng } = userLocation.coordinates;

    if (lat && lng) {
      setMapCenter([lat, lng]);
      setZoomLevel(DEFAULT_ZOOM_USER);
      debouncedFetchLocations(lat, lng, false);
    } else if (userLocation.error) {
      setLoading(false);
      setError(`Kh√¥ng th·ªÉ x√°c ƒë·ªãnh v·ªã tr√≠: ${userLocation.error}`);
    }
  }, [
    userLocation.loaded,
    userLocation.coordinates,
    userLocation.error,
    isAdminMode,
    debouncedFetchLocations,
  ]);

  if (loading) return <p>ƒêang t·∫£i d·ªØ li·ªáu b·∫£n ƒë·ªì...</p>;

  return (
    <div style={{ height: '800px', width: '100%', position: 'relative' }}>
      {error && <p style={{ color: 'red', padding: '10px' }}>{error}</p>}

      {/* üëë N√∫t chuy·ªÉn ch·∫ø ƒë·ªô (ch·ªâ hi·ªán v·ªõi admin th·∫≠t) */}
      {isRealAdmin && (
        <div style={{ position: 'absolute', top: 20, right: 20, zIndex: 1000 }}>
          <Button
            variant="outline"
            onClick={() => setIsAdminMode((prev) => !prev)}
            className="shadow-md rounded-xl px-4 py-2"
          >
            {isAdminMode ? 'üëÅÔ∏è Xem nh∆∞ ng∆∞·ªùi d√πng' : 'üó∫Ô∏è Xem t·∫•t c·∫£ ƒë·ªãa ƒëi·ªÉm'}
          </Button>
        </div>
      )}

      <MapContainer
        center={mapCenter}
        zoom={zoomLevel}
        scrollWheelZoom
        style={{ height: '100%', width: '100%' }}
        maxZoom={20}
      >
        <SmoothChangeView center={mapCenter} zoom={zoomLevel} />

        <TileLayer
          attribution='&copy; <a href="https://maps.google.com">Google Maps</a>'
          url="https://mt1.google.com/vt/lyrs=m&x={x}&y={y}&z={z}"
          maxZoom={20}
          maxNativeZoom={17}
        />

        {/* üë§ User only: Hi·ªán v·ªã tr√≠ hi·ªán t·∫°i */}
        {!isAdminMode && userLocation.coordinates.lat && (
          <>
            <Marker position={[userLocation.coordinates.lat, userLocation.coordinates.lng]}>
              <Popup>V·ªã tr√≠ hi·ªán t·∫°i c·ªßa b·∫°n</Popup>
            </Marker>
            <Circle
              center={[userLocation.coordinates.lat, userLocation.coordinates.lng]}
              radius={1000}
              pathOptions={{
                color: 'blue',
                fillColor: 'lightblue',
                fillOpacity: 0.25,
              }}
            />
          </>
        )}

        {/* üìç C√°c ƒë·ªãa ƒëi·ªÉm */}
        {locations.map((loc) => {
          const lat = parseFloat(loc.latitude);
          const lng = parseFloat(loc.longitude);
          if (isNaN(lat) || isNaN(lng)) return null;

          return (
            <Marker
              key={loc.id}
              position={[lat, lng]}
              title={loc.name}
              eventHandlers={{
                click: () => setSelectedLocation(loc),
              }}
            >
              <Popup>
                <strong>{loc.name}</strong>
                <br />
                {isAdminMode
                  ? 'ƒê·ªãa ƒëi·ªÉm trong h·ªá th·ªëng'
                  : `Kho·∫£ng c√°ch: ${loc.distance_km?.toFixed(2)} km`}
              </Popup>
            </Marker>
          );
        })}
      </MapContainer>

      {/* Modal chi ti·∫øt */}
      {selectedLocation && (
        <div
          className={`detail-modal ${selectedLocation.fadeOut ? 'fade-out' : ''}`}
          onAnimationEnd={() => {
            if (selectedLocation.fadeOut) setSelectedLocation(null);
          }}
        >
          <div className={`detail-content ${selectedLocation.fadeOut ? 'fade-out-content' : ''}`}>
            <button
              className="detail-close"
              onClick={() => setSelectedLocation((prev) => ({ ...prev, fadeOut: true }))}
            >
              √ó
            </button>

            <h2>{selectedLocation.name}</h2>
            {selectedLocation.address && (
              <p>
                <strong>ƒê·ªãa ch·ªâ:</strong> {selectedLocation.address}
              </p>
            )}
            {selectedLocation.description && <p>{selectedLocation.description}</p>}
          </div>
        </div>
      )}
    </div>
  );
};

export default LeafletMapComponent;
