import React, { useState, useEffect } from 'react';
import './App.css';
import LeafletMapComponent from './MapContainer';
import LocationCRUD from './LocationCRUD';
import { AuthProvider, useAuth } from './context/AuthContext';
import AuthModal from './pages/AuthModal';

function MainApp() {
  const { authToken, userRole, login, logout } = useAuth();
  const [showModal, setShowModal] = useState(false);

  // Giữ trạng thái đăng nhập khi reload
  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    if (token && role) login(token, role);
  }, [login]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Bản đồ Ẩm thực Hà Nội</h1>

        {!authToken ? (
          <button
            onClick={() => setShowModal(true)}
            className="ml-4 bg-black text-white px-4 py-2 rounded hover:bg-gray-800"
          >
            Đăng nhập / Đăng ký
          </button>
        ) : (
          <div>
            <p>Vai trò: {userRole}</p>
            <button
              onClick={logout}
              className="ml-4 bg-gray-600 text-white px-3 py-1 rounded hover:bg-gray-700"
            >
              Đăng xuất
            </button>
          </div>
        )}
      </header>

      {/* Chỉ hiển thị CRUD nếu là admin */}
      {authToken && userRole === 'admin' && <LocationCRUD />}

      <div style={{ padding: '20px' }}>
        <LeafletMapComponent />
      </div>

      {/* Hiển thị popup nếu người dùng nhấn đăng nhập */}
      {showModal && <AuthModal onClose={() => setShowModal(false)} />}
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <MainApp />
    </AuthProvider>
  );
}

export default App;
