import React, { useState } from 'react';
import { Outlet } from 'react-router-dom'; // üëà QUAN TR·ªåNG: Ph·∫£i import c√°i n√†y
import Header from './Header';
import Sidebar from './Sidebar';
import Footer from './Footer';
import AuthModal from '../pages/AuthModal';

// ‚ùå ƒê·ª´ng import LeafletMapComponent ·ªü ƒë√¢y!
// import LeafletMapComponent from '../MapContainer'; 

export default function MainLayout() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [showAuthModal, setShowAuthModal] = useState(false);

  return (
    <div className="app-container">
      <Header 
        onToggleSidebar={() => setIsSidebarOpen(!isSidebarOpen)} 
        onOpenAuth={() => setShowAuthModal(true)}
      />

      <div className="body-container">
        <Sidebar 
          isOpen={isSidebarOpen} 
          onCloseMobile={() => setIsSidebarOpen(false)} 
        />
        
        <main className="main-content">
          {/* üî¥ SAI: N·∫øu b·∫°n ƒë·ªÉ <LeafletMapComponent /> ·ªü ƒë√¢y, 
             n√≥ s·∫Ω lu√¥n hi·ªán map d√π b·∫°n sang trang admin.
          */}

          {/* üü¢ ƒê√öNG: D√πng Outlet. 
             - N·∫øu url l√† "/" -> Outlet s·∫Ω hi·ªán Map.
             - N·∫øu url l√† "/admin" -> Outlet s·∫Ω hi·ªán LocationCRUD.
          */}
          <Outlet /> 
        </main>
      </div>

      <Footer />
      {showAuthModal && <AuthModal onClose={() => setShowAuthModal(false)} />}
    </div>
  );
}
