import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';

// Components & Pages
import MainLayout from './components/MainLayout';
import LeafletMapComponent from './MapContainer';
// üëá ƒê·∫£m b·∫£o ƒë∆∞·ªùng d·∫´n n√†y ƒë√∫ng v·ªõi m√°y b·∫°n (src/LocationCRUD.js hay src/pages/LocationCRUD.js?)
import LocationCRUD from './pages/LocationCRUD';
import LocationListPage from './pages/LocationListPage'; // ‚ú® TH√äM D√íNG N√ÄY
import LocationDetailPage from './pages/LocationDetailPage.jsx';

import LandingPage from './pages/LandingPage.jsx';
import ProtectedRoute from './components/ProtectedRoute';

import { AuthProvider, useAuth } from './context/AuthContext';

// --- TRANG B√ÅO L·ªñI QUY·ªÄN (Component nh·ªè n·ªôi b·ªô) ---
function UnauthorizedPage() {
  return (
    <div style={{ padding: 50, textAlign: 'center', marginTop: 50 }}>
      <h1>‚õî Truy c·∫≠p b·ªã t·ª´ ch·ªëi</h1>
      <p>B·∫°n c·∫ßn quy·ªÅn <b>Qu·∫£n tr·ªã vi√™n (Admin)</b> ƒë·ªÉ truy c·∫≠p trang n√†y.</p>
      <a href="/" style={{ color: 'blue', textDecoration: 'underline' }}>Quay l·∫°i trang ch·ªß</a>
    </div>
  );
}

function AppRoutes() {
  const { user } = useAuth();

  // N·∫øu ch∆∞a ƒëƒÉng nh·∫≠p -> Hi·ªán Landing Page
  if (!user) {
    return <LandingPage />;
  }

  return (
    <Routes>
      <Route path="/" element={<MainLayout />}>
        {/* M·∫∑c ƒë·ªãnh hi·ªán Map */}
        <Route index element={<LeafletMapComponent />} />

        {/* ‚ú® TH√äM ROUTE CHO TRANG CHI TI·∫æT */}
        <Route path="nearby" element={<LocationDetailPage />} />
        
        {/* Route Admin ƒë∆∞·ª£c b·∫£o v·ªá */}
        <Route 
          path="admin" 
          element={
            <ProtectedRoute requiredRole="admin">
               <div style={{ padding: '20px', overflowY: 'auto', height: '100%', width: '100%' }}>
                  {/* Render b·∫£ng qu·∫£n l√Ω */}
                  <LocationCRUD />
               </div>
            </ProtectedRoute>
          } 
        />
        
        {/* üëá TH√äM ROUTE N√ÄY */}
        <Route path="unauthorized" element={<UnauthorizedPage />} />

        {/* Catch-all: V·ªÅ trang ch·ªß */}
        <Route path="*" element={<Navigate to="/" />} />
      </Route>
    </Routes>
  );
}

function App() {
  return (
    <Router>
      <AuthProvider>
         <AppRoutes />
      </AuthProvider>
    </Router>
  );
}

export default App;
