import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

export default function AuthModal({ onClose }) {
  const { login } = useAuth();
  const [isLogin, setIsLogin] = useState(true);
  const [form, setForm] = useState({ email: '', password: '', username: '' });
  const [message, setMessage] = useState('');

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await axios.post('http://localhost:5000/api/auth/login', {
          email: form.email,
          password: form.password,
        });
        login(res.data.token, res.data.role);
        localStorage.setItem('token', res.data.token);
        localStorage.setItem('role', res.data.role);
        setMessage('Đăng nhập thành công!');
        onClose(); // đóng popup
      } else {
        await axios.post('http://localhost:5000/api/auth/register', {
          email: form.email,
          password: form.password,
          username: form.username,
        });
        setMessage('Đăng ký thành công! Hãy đăng nhập.');
        setIsLogin(true);
      }
    } catch (err) {
      setMessage(err.response?.data?.message || 'Có lỗi xảy ra.');
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex justify-center items-center z-50">
      <div className="bg-white rounded-xl shadow-lg w-[600px] max-w-[90%] relative">
        <button
          onClick={onClose}
          className="absolute top-3 right-4 text-gray-600 text-2xl hover:text-black"
        >
          &times;
        </button>

        <div className="flex flex-col md:flex-row">
          {/* Đăng nhập */}
          <div className="md:w-1/2 w-full p-6 border-r">
            <h2 className="text-2xl font-bold mb-4">ĐĂNG NHẬP</h2>
            <form onSubmit={handleSubmit}>
              <label className="block mb-2 font-semibold">Email*</label>
              <input
                type="email"
                name="email"
                placeholder="Nhập email"
                onChange={handleChange}
                className="w-full border p-2 mb-3 rounded"
                required
              />

              <label className="block mb-2 font-semibold">Mật khẩu*</label>
              <input
                type="password"
                name="password"
                placeholder="Nhập mật khẩu"
                onChange={handleChange}
                className="w-full border p-2 mb-4 rounded"
                required
              />

              {message && <p className="text-red-600 mb-2">{message}</p>}

              <button
                type="submit"
                className="w-full bg-black text-white py-2 font-semibold rounded hover:bg-gray-800"
              >
                {isLogin ? 'Đăng nhập' : 'Tạo tài khoản'}
              </button>
            </form>
          </div>

          {/* Tạo tài khoản */}
          <div className="md:w-1/2 w-full p-6 bg-gray-50">
            <h2 className="text-2xl font-bold mb-3">TẠO TÀI KHOẢN</h2>
            <p className="text-sm mb-4">
              Tạo tài khoản để lưu địa điểm yêu thích và nhận thông báo mới nhất.
            </p>

            {!isLogin && (
              <input
                type="text"
                name="username"
                placeholder="Tên người dùng"
                onChange={handleChange}
                className="w-full border p-2 mb-3 rounded"
                required
              />
            )}

            <button
              onClick={() => setIsLogin(!isLogin)}
              className="w-full bg-black text-white py-2 font-semibold rounded hover:bg-gray-800"
            >
              {isLogin ? 'Tạo tài khoản mới' : 'Quay lại đăng nhập'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
