import React, { useEffect, useState } from 'react';
import './App.css';
import LeafletMapComponent from './MapContainer';
import LocationCRUD from './LocationCRUD';
import { AuthProvider, useAuth } from './context/AuthContext';
import AuthModal from './pages/AuthModal';

function MainApp() {
  const { authToken, logout, userRole, login } = useAuth();
  const [showAuthModal, setShowAuthModal] = useState(false);

  // üåû Theme (m·∫∑c ƒë·ªãnh l√† light)
  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light');

  useEffect(() => {
    if (theme === 'light') {
      document.body.classList.add('light-mode');
      document.body.classList.remove('dark-mode');
    } else {
      document.body.classList.add('dark-mode');
      document.body.classList.remove('light-mode');
    }
    localStorage.setItem('theme', theme);
  }, [theme]);

  // üëá TH√äM ƒêO·∫†N N√ÄY: Lu√¥n cu·ªôn l√™n ƒë·∫ßu trang khi t·∫£i app
  useEffect(() => {
    // T·∫Øt t√≠nh nƒÉng t·ª± nh·ªõ v·ªã tr√≠ cu·ªôn c·ªßa tr√¨nh duy·ªát
    if ('scrollRestoration' in window.history) {
      window.history.scrollRestoration = 'manual';
    }
    // Cu·ªôn l√™n ƒë·∫ßu trang ngay l·∫≠p t·ª©c
    window.scrollTo(0, 0);
  }, []);

  // üîë Gi·ªØ tr·∫°ng th√°i ƒëƒÉng nh·∫≠p
  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    if (token && role) login(token, role);
  }, [login]);

  return (
    <div className="App">
      {/* ‚òÄÔ∏è / üåô N√∫t chuy·ªÉn theme */}
      <button
        className="theme-toggle"
        onClick={() => setTheme(theme === 'light' ? 'dark' : 'light')}
        title={theme === 'light' ? 'Chuy·ªÉn sang t·ªëi' : 'Chuy·ªÉn sang s√°ng'}
      >
        {theme === 'light' ? '‚òÄÔ∏è' : 'üåô'}
      </button>

      <header className="App-header">
        <h1>B·∫£n ƒë·ªì ·∫®m th·ª±c H√† N·ªôi</h1>

        {!authToken ? (
          <button
            onClick={() => setShowAuthModal(true)}
            className="login-btn"
          >
            ƒêƒÉng nh·∫≠p / ƒêƒÉng k√Ω
          </button>
        ) : (
          <div>
            <p>Vai tr√≤: {userRole}</p>
            <button onClick={logout} className="login-btn">
              ƒêƒÉng xu·∫•t
            </button>
          </div>
        )}
      </header>

      {/* CRUD ch·ªâ hi·ªÉn th·ªã khi l√† admin */}
      {authToken && userRole === 'admin' && <LocationCRUD />}

      <div style={{ padding: '20px' }}>
        <LeafletMapComponent />
      </div>

      {/* Modal ƒëƒÉng nh·∫≠p / ƒëƒÉng k√Ω */}
      {showAuthModal && <AuthModal onClose={() => setShowAuthModal(false)} />}
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <MainApp />
    </AuthProvider>
  );
}

export default App;
