import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

export default function LoginPage() {
  const { login } = useAuth();
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ email: '', password: '', username: '' });
  const [message, setMessage] = useState('');

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        // gọi API đăng nhập
        const res = await axios.post('http://localhost:5000/api/auth/login', {
          email: formData.email,
          password: formData.password,
        });

        // Lưu token và role vào AuthContext
        login(res.data.token, res.data.role);

        // Lưu tạm token (để reload vẫn giữ login)
        localStorage.setItem('token', res.data.token);
        localStorage.setItem('role', res.data.role);
        window.location.reload();

      } else {
        // Gọi API đăng ký
        await axios.post('http://localhost:5000/api/auth/register', {
          email: formData.email,
          password: formData.password,
          username: formData.username,
        });
        setMessage('Tạo tài khoản thành công! Hãy đăng nhập.');
        setIsLogin(true);
      }
    } catch (err) {
      setMessage(err.response?.data?.message || 'Có lỗi xảy ra.');
    }
  };

  return (
    <div className="flex min-h-screen items-center justify-center bg-white">
      <div className="flex w-full max-w-5xl border rounded-md shadow-md">
        {/* Đăng nhập */}
        <div className="w-1/2 p-10 border-r">
          <h2 className="text-3xl font-bold mb-6">ĐĂNG NHẬP</h2>
          <form onSubmit={handleSubmit}>
            <label className="block mb-2 font-semibold">ĐỊA CHỈ EMAIL*</label>
            <input
              type="email"
              name="email"
              placeholder="Nhập email hợp lệ"
              onChange={handleChange}
              className="w-full border p-2 mb-4"
              required
            />

            <label className="block mb-2 font-semibold">MẬT KHẨU*</label>
            <input
              type="password"
              name="password"
              placeholder="Nhập mật khẩu"
              onChange={handleChange}
              className="w-full border p-2 mb-4"
              required
            />

            {message && <p className="text-red-600 mb-3">{message}</p>}

            <button
              type="submit"
              className="w-full bg-black text-white py-2 font-bold hover:bg-gray-800"
            >
              {isLogin ? 'ĐĂNG NHẬP' : 'TẠO TÀI KHOẢN'}
            </button>

            {isLogin && (
              <p className="mt-4 text-sm underline cursor-pointer">
                Quên mật khẩu của bạn?
              </p>
            )}
          </form>
        </div>

        {/* Đăng ký */}
        <div className="w-1/2 p-10 bg-gray-50">
          <h2 className="text-3xl font-bold mb-4">TẠO MỘT TÀI KHOẢN</h2>
          <p className="mb-6 text-sm">
            Hãy tạo tài khoản ngay! Bạn có thể nhận thông báo và chia sẻ địa điểm yêu thích trong Bản đồ Ẩm thực Hà Nội.
          </p>

          {!isLogin && (
            <input
              type="text"
              name="username"
              placeholder="Tên người dùng"
              onChange={handleChange}
              className="w-full border p-2 mb-4"
              required
            />
          )}

          <button
            onClick={() => setIsLogin(!isLogin)}
            className="w-full bg-black text-white py-2 font-bold hover:bg-gray-800"
          >
            {isLogin ? 'TẠO TÀI KHOẢN' : 'QUAY LẠI ĐĂNG NHẬP'}
          </button>
        </div>
      </div>
    </div>
  );
}
