import React, { useEffect, useState } from 'react';
import './App.css';
import LeafletMapComponent from './MapContainer';
import LocationCRUD from './LocationCRUD';
import { AuthProvider, useAuth } from './context/AuthContext';
import AuthModal from './pages/AuthModal';

function MainApp() {
  const { authToken, logout, userRole, login } = useAuth();
  const [showAuthModal, setShowAuthModal] = useState(false);
  
  // State ƒë·ªÉ hi·ªÉn th·ªã t√™n ng∆∞·ªùi d√πng
  const [username, setUsername] = useState('');

  // üåû Theme (m·∫∑c ƒë·ªãnh l√† light)
  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light');

  useEffect(() => {
    if (theme === 'light') {
      document.body.classList.add('light-mode');
      document.body.classList.remove('dark-mode');
    } else {
      document.body.classList.add('dark-mode');
      document.body.classList.remove('light-mode');
    }
    localStorage.setItem('theme', theme);
  }, [theme]);

  // üëá Lu√¥n cu·ªôn l√™n ƒë·∫ßu trang khi t·∫£i app
  useEffect(() => {
    if ('scrollRestoration' in window.history) {
      window.history.scrollRestoration = 'manual';
    }
    window.scrollTo(0, 0);
  }, []);

  // üîë Gi·ªØ tr·∫°ng th√°i ƒëƒÉng nh·∫≠p & C·∫≠p nh·∫≠t Username
  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    const savedName = localStorage.getItem('username'); // L·∫•y t√™n t·ª´ b·ªô nh·ªõ

    if (token && role) {
      login(token, role);
    }
    
    if (savedName) {
      setUsername(savedName);
    }
  }, [login]);

  // C·∫≠p nh·∫≠t username khi authToken thay ƒë·ªïi (ƒë·ªÉ UI c·∫≠p nh·∫≠t ngay khi login xong)
  useEffect(() => {
    if (authToken) {
      const name = localStorage.getItem('username');
      if (name) setUsername(name);
    } else {
      setUsername('');
    }
  }, [authToken]);

  // H√†m ƒëƒÉng xu·∫•t m·ªü r·ªông (x√≥a c·∫£ username)
  const handleLogout = () => {
    logout();
    localStorage.removeItem('username');
    setUsername('');
  };

  return (
    <div className="App">
      {/* ‚òÄÔ∏è / üåô N√∫t chuy·ªÉn theme */}
      <button
        className="theme-toggle"
        onClick={() => setTheme(theme === 'light' ? 'dark' : 'light')}
        title={theme === 'light' ? 'Chuy·ªÉn sang t·ªëi' : 'Chuy·ªÉn sang s√°ng'}
      >
        {theme === 'light' ? '‚òÄÔ∏è' : 'üåô'}
      </button>

      <header className="App-header">
        <h1>B·∫£n ƒë·ªì ·∫®m th·ª±c H√† N·ªôi</h1>

        {!authToken ? (
          <button
            onClick={() => setShowAuthModal(true)}
            className="login-btn"
          >
            ƒêƒÉng nh·∫≠p / ƒêƒÉng k√Ω
          </button>
        ) : (
          <div>
            {/* üëá Hi·ªÉn th·ªã l·ªùi ch√†o */}
            <h3 style={{ margin: '0 0 10px 0', color: '#fff', textShadow: '0 2px 4px rgba(0,0,0,0.5)' }}>
              Xin ch√†o, {username || 'B·∫°n'}!
            </h3>
            <p>Vai tr√≤: {userRole === 'admin' ? 'Qu·∫£n tr·ªã vi√™n' : 'Th√†nh vi√™n'}</p>
            
            <button onClick={handleLogout} className="login-btn">
              ƒêƒÉng xu·∫•t
            </button>
          </div>
        )}
      </header>

      {/* CRUD ch·ªâ hi·ªÉn th·ªã khi l√† admin */}
      {authToken && userRole === 'admin' && <LocationCRUD />}

      <div style={{ padding: '20px' }}>
        <LeafletMapComponent />
      </div>

      {/* Modal ƒëƒÉng nh·∫≠p / ƒëƒÉng k√Ω */}
      {showAuthModal && <AuthModal onClose={() => setShowAuthModal(false)} />}
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <MainApp />
    </AuthProvider>
  );
}

export default App;
