// frontend/src/App.js
import React, { useEffect } from 'react';
import './App.css';
import LeafletMapComponent from './MapContainer';
import LocationCRUD from './LocationCRUD';
import { AuthProvider, useAuth } from './context/AuthContext';
import LoginPage from './pages/LoginPage';

function MainApp() {
  const { authToken, logout, userRole, login } = useAuth();

  // Giữ trạng thái login sau khi reload
  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    if (token && role) login(token, role);
  }, [login]);

  if (!authToken) return <LoginPage />;

  return (
    <div className="App">
      <header className="App-header">
        <h1>Bản đồ Ẩm thực Hà Nội</h1>
        <p>Vai trò: {userRole}</p>
        <button onClick={logout} style={{ marginTop: 10 }}>Đăng xuất</button>
      </header>

      {/* Chỉ Admin mới thấy CRUD */}
      {userRole === 'admin' && <LocationCRUD />}

      <div style={{ padding: '20px' }}>
        <LeafletMapComponent />
      </div>
    </div>
  );
}

function App() {
  return (
    <AuthProvider>
      <MainApp />
    </AuthProvider>
  );
}

export default App;
